requestHeader:[host:&amp;quot;localhost:8080&amp;quot;, connection:&amp;quot;keep-alive&amp;quot;, content-length:&amp;quot;27&amp;quot;, cache-control:&amp;quot;max-age=0&amp;quot;, sec-ch-ua:&amp;quot;&amp;quot; Not A;Brand&amp;quot;;v=&amp;quot;99&amp;quot;, &amp;quot;Chromium&amp;quot;;v=&amp;quot;90&amp;quot;, &amp;quot;Google Chrome&amp;quot;;v=&amp;quot;90&amp;quot;&amp;quot;, sec-ch-ua-mobile:&amp;quot;?0&amp;quot;, upgrade-insecure-requests:&amp;quot;1&amp;quot;, origin:&amp;quot;&lt;span class=&#34;exturl&#34; data-url=&#34;aHR0cDovL2xvY2FsaG9zdDo4MDgw&#34;&gt;http://localhost:8080&lt;/span&gt;&amp;quot;, user-agent:&amp;quot;Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/90.0.4430.93 Safari/537.36&amp;quot;]&lt;br /&gt;
